script_shortcuts.action:
Always_on_Top()
Always_on_Top(){
    static alwaysOnTop := false
    if (alwaysOnTop) {
        WinSetAlwaysOnTop(false, "A")
    } else {
        WinSetAlwaysOnTop(true, "A")
    }
    alwaysOnTop := !alwaysOnTop
}

KillForeground()
KillForeground() {
    MouseGetPos(, , &WinID)
    ProcessID := WinGetPID("ahk_id " WinID)
    Run("taskkill /f /pid " ProcessID, , "Hide")
}
